mapper.sql:
SELECT * FROM ( SELECT SUBSTR(a.bill_date, 1, 10) AS billDate, sum(a.check_amount) AS checkAmount, sum(a.payment_amount) AS paymentAmount, GROUP_CONCAT(a.bill_no) AS billNo, count(a.bill_no) AS billCount FROM rp_order a WHERE a.bill_date BETWEEN #{param.start} and #{param.end} and a.bill_type = #{param.billType} and a.shop_no in #{item}
GROUP BY SUBSTR(a.bill_date, 1, 10) ) aa LEFT JOIN ( SELECT SUBSTR(c.trade_time, 1, 10) AS tradeTime, sum(c.pay_amount) AS payAmount FROM cashier_record c WHERE c.trade_time BETWEEN #{param.start} and #{param.end} and c.pay_direct = #{param.payDirect} and c.cost_type in #{item}
and c.shop_no in #{item}
GROUP BY SUBSTR(c.trade_time, 1, 10) ) bb ON aa.billDate = bb.tradeTime RIGHT JOIN ( SELECT SUBSTR(c.trade_time, 1, 10) AS tradeTimeR, sum(c.pay_amount) AS payAmountR FROM cashier_record c WHERE c.trade_time BETWEEN #{param.start} and #{param.end} and c.pay_direct = #{param.payDirect} and c.cost_type in #{item}
and c.shop_no in #{item}
GROUP BY SUBSTR(c.trade_time, 1, 10) ) cc ON aa.billDate = cc.tradeTimeR

mapper.select:
SELECT AS billDate, sum(kk.checkAmount) AS checkAmount, sum(kk.paymentAmount) AS paymentAmount, sum(ifnull(kk.payAmount, kk.payAmountR)) AS payAmount, group_concat(kk.billNo) AS billNo, sum(kk.billCount) AS billCount FROM (
) kk GROUP BY
ORDER BY kk.billDate desc
